mapper.select:
select app.id, app.name, app.url, app.icon_url, app.category_id, category.name as category_name, app.description, app.weight, app.click_count from app left join category on app.category_id = category.id
app.create_user = #{userId}
and app.status <= #{status}
and app.category_id = #{categoryId} or app.category_id in (select id from category where (parent_id = #{categoryId} or id = #{categoryId}))
and category.status = 1
order by app.weight desc

select app.id, app.name, app.url, app.icon_url, app.category_id, category.name as category_name, app.description, app.weight, app.click_count, app.status from app left join category on app.category_id = category.id
app.create_user = #{userId}
and app.status = #{status}
and app.category_id = #{categoryId}
and ( app.name like concat('%', #{searchContent}, '%') or app.description like concat('%', #{searchContent}, '%') )
order by app.weight desc
